import "timezone"
import "date"

option location = timezone.location(name: "Europe/Rome")

today_start = date.truncate(t: now(), unit: 1d)

from(bucket: "Solaredge_Realtime")
  |> range(start: today_start)
  |> filter(fn: (r) => r["_measurement"] == "realtime")
  |> filter(fn: (r) => r["_field"] == "Meter")
  |> filter(fn: (r) => r["endpoint"] == "Power")
  // Nessuna aggregazione, dati grezzi
  |> map(fn: (r) => ({
      _time: r._time,
      // Se è negativo (prelievo) mettiamo 0, altrimenti teniamo il valore (immissione)
      "Immissione": if r._value < 0.0 then 0.0 else r._value
    }))
  // Opzionale: filtrare via gli zeri per alleggerire il grafico se vuoi vedere solo quando immetti davvero
  // |> filter(fn: (r) => r.Immissione > 0.0)
  |> yield(name: "immissione_raw")

import "timezone"
import "date"

option location = timezone.location(name: "Europe/Rome")

today_start = date.truncate(t: now(), unit: 1d)

from(bucket: "Solaredge_Realtime")
  |> range(start: today_start)
  |> filter(fn: (r) =>
      r._measurement == "realtime" and
      ((r._field == "Inverter" and r.endpoint == "Power Ac") or
       (r._field == "Meter" and r.endpoint == "Power"))
  )
  // Allineamento temporale (fondamentale per calcoli tra serie diverse)
  |> aggregateWindow(every: 5s, fn: mean, createEmpty: false)
  
  // Pivot invece di Join
  |> keep(columns: ["_time", "_value", "_field"])
  |> pivot(rowKey:["_time"], columnKey: ["_field"], valueColumn: "_value")
  
  // Calcolo Autoconsumo
  |> map(fn: (r) => ({
      _time: r._time,
      _prod: if exists r.Inverter then r.Inverter else 0.0,
      _meter: if exists r.Meter then r.Meter else 0.0
  }))
  |> map(fn: (r) => ({
      _time: r._time,
      Autoconsumo: if r._meter >= 0.0 
                   then r._prod - r._meter  // Se esporto: Prod - Export
                   else r._prod             // Se importo: Tutto quello che produco lo consumo
  }))
  // Filtro valori negativi spuri (non dovrebbe succedere, ma per sicurezza)
  |> filter(fn: (r) => r.Autoconsumo >= 0.0)
  |> yield(name: "autoconsumo_ottimizzato")

import "timezone"
import "date"

option location = timezone.location(name: "Europe/Rome")

// Usa il time range selezionato in Grafana
// Puoi usare "Last 1 year" oppure selezionare un range custom
startOfPeriod = date.truncate(t: v.timeRangeStart, unit: 1y)
endOfPeriod = date.add(d: 1y, to: startOfPeriod)

from(bucket: "Solaredge")
  |> range(start: startOfPeriod, stop: endOfPeriod)
  |> filter(fn: (r) => r["_measurement"] == "web" and r["_field"] == "Site")
  |> filter(fn: (r) => 
      r["endpoint"] == "PRODUCTION_ENERGY" or 
      r["endpoint"] == "IMPORT_ENERGY" or 
      r["endpoint"] == "EXPORT_ENERGY"
  )
  
  // Aggreghiamo per mese usando il fuso orario configurato
  |> aggregateWindow(
      every: 1mo, 
      fn: sum, 
      createEmpty: true,
      timeSrc: "_start",
      location: location
  )
  
  // Riempiamo eventuali valori mancanti con zero
  |> fill(value: 0.0)
  
  // Trasformiamo da formato lungo a formato largo
  |> pivot(rowKey: ["_time"], columnKey: ["endpoint"], valueColumn: "_value")
  
  // Gestiamo eventuali valori null dopo il pivot
  |> map(fn: (r) => ({ r with 
      prod: if exists r.PRODUCTION_ENERGY then r.PRODUCTION_ENERGY else 0.0,
      imp: if exists r.IMPORT_ENERGY then r.IMPORT_ENERGY else 0.0,
      exp: if exists r.EXPORT_ENERGY then r.EXPORT_ENERGY else 0.0
  }))
  
  // Calcoliamo le metriche derivate
  |> map(fn: (r) => ({ 
      _time: r._time,
      Produzione: r.prod,
      Prelievo: r.imp,
      Immissione: r.exp,
      Consumo: r.prod + r.imp - r.exp,
      Autoconsumo: r.prod - r.exp
  }))
  
  // Filtriamo solo i dati entro il periodo selezionato
  |> filter(fn: (r) => r._time < endOfPeriod)

