import "timezone"
import "date"

option location = timezone.location(name: "Europe/Rome")

// CAMBIA QUI LA METRICA DA CONFRONTARE:
// PRODUCTION_ENERGY, IMPORT_ENERGY, EXPORT_ENERGY, ecc.
target_metric = "PRODUCTION_ENERGY"

from(bucket: "Solaredge")
  // Prendi tutto lo storico
  |> range(start: 0, stop: now())
  |> filter(fn: (r) => r["_measurement"] == "web" and r["_field"] == "Site")
  |> filter(fn: (r) => r["endpoint"] == target_metric)
  
  // Aggregazione mensile con fuso orario corretto
  |> aggregateWindow(
      every: 1mo, 
      fn: sum, 
      createEmpty: false,
      timeSrc: "_start",
      location: location
  )
  
  // Estrai Anno e Mese dal timestamp aggregato
  |> map(fn: (r) => ({ 
      r with 
      year: string(v: date.year(t: r._time)),
      month: date.month(t: r._time)
  }))
  
  // Mappa i nomi dei mesi
  |> map(fn: (r) => ({
      r with
      month_name: if r.month == 1 then "Gennaio"
        else if r.month == 2 then "Febbraio"
        else if r.month == 3 then "Marzo"
        else if r.month == 4 then "Aprile"
        else if r.month == 5 then "Maggio"
        else if r.month == 6 then "Giugno"
        else if r.month == 7 then "Luglio"
        else if r.month == 8 then "Agosto"
        else if r.month == 9 then "Settembre"
        else if r.month == 10 then "Ottobre"
        else if r.month == 11 then "Novembre"
        else "Dicembre"
  }))
  
  // Pivot per avere gli anni come colonne
  |> group()
  |> pivot(rowKey: ["month", "month_name"], columnKey: ["year"], valueColumn: "_value")
  |> sort(columns: ["month"])
  |> drop(columns: ["month"])
